// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;

contract Loop {
    function loop() public pure {
        // For Loop
        for (uint256 i = 0; i < 10; i++) {
            if (i == 3) {
                // Skip to next iteration with continue
                continue;
            }
            if (i == 5) {
                // Exit loop with break
                break;
            }
        }
        // while loop
        uint256 j;
        while (j < 10) {
            j++;
        }
    }
}
